    public function getSourceContext()
    {
        return new Source("{% extends 'baselayout.html.twig' %}

{% block title %}Reservation index{% endblock %}

{% block main %}
    <h1>Reservation index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Room</th>
                <th>Client</th>
                <th>Arrival</th>
                <th>Departure</th>
                <th>Confirmed</th>
                <th>Additional_info</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
        {% for reservation in reservations %}
            <tr>
                <td>{{ reservation.id }}</td>
                <td>{{ reservation.room }}</td>
                <td>{{ reservation.client.user.email }}</td>                
                <td>{{ reservation.arrival ? reservation.arrival|date('Y-m-d') : '' }}</td>
                <td>{{ reservation.departure ? reservation.departure|date('Y-m-d') : '' }}</td>
                <td>{{ reservation.confirmed ? 'Yes' : 'No' }}</td>
                <td>{{ reservation.additionalInfo }}</td>
                <td>
                    <a href=\"{{ path('reservation_show', {'id': reservation.id}) }}\">show</a>
                    <a href=\"{{ path('reservation_edit', {'id': reservation.id}) }}\">edit</a>
                    {% if reservation.confirmed %}
                    \t<a href=\"{{ path('reservation_confirm', {'id': reservation.id}) }}\">unconfirm</a>
                    {% else %}
                    \t<a href=\"{{ path('reservation_confirm', {'id': reservation.id}) }}\">confirm</a>
\t\t\t\t\t{% endif %}
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"6\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('reservation_new') }}\">Create new</a>
{% endblock %}
", "reservation/index.html.twig", "/home/geoffroy/Documents/CSC4101/proj-agvoy/agvoy-app-03/templates/reservation/index.html.twig");
    }
